import { useEffect, useState, useWindowSize } from 'react'

import NavMenuButton from '../../buttons/nav-menu-button/NavMenuButton'
import './NavBar.css'

export default function NavBar() {
  const [R6LogoSize, setR6LogoSize] = useState('small')
  const viewSize = useWindowSize()

  useEffect(() => {
    if (viewSize.width >= 800) {
      setR6LogoSize('large')
    } else {
      setR6LogoSize('small')
    }
  }, [viewSize])

  return (
    <div className="NavBar">
      <div className="AppTitle">
        <img id="r6s-logo" alt="Rainbow Six: Siege" src={`images/ui/siege-logo-${R6LogoSize}.png`}/>
        <h1>Info Suite</h1>
      </div>
      <NavMenuButton />
    </div>
  )
}
