export default function Blueprint(props) {
    const { mapName, floor, floorData } = props

    function handleClick(e) {
        e.currentTarget.classList.add("highlighted")
    }

    return (
        <div className="Blueprint">
            <div className="blueprint-image">
                <img 
                    alt={`${mapName} Blueprint - ${floor}`} 
                    src={`/images/maps/${mapName}/${mapName}-${floor}.webp`} 
                    useMap="#workmap"
                />
                <map className="blueprint-map" name="workmap">
                    { floorData.map(item => <area shape={item.shape} coords={item.coords.flat().toString()} alt={item.room} key={item.room} onClick={handleClick}/>) }
                </map>
            </div>
            <div className="blueprint-buttons">
                <svg width="100%" height="100%" version="1.1" xmlns="http://www.w3.org/2000/svg">
                    <polygon className="hoverable" points="468,171 546,171 546,186 716,186 716,171 784,171 784,245 468,245" />
                </svg>
            </div>
        </div>
    )
}
