import { useEffect, useState } from 'react'

import NavMenuButton from '../../buttons/nav-menu-button/NavMenuButton'
import './NavBar.css'

export default function NavBar() {
  const [R6LogoSize, setR6LogoSize] = useState('small')
  const [showSettings, setShowSettings] = useState(false)
  const [windowSize, setWindowSize] = useState(window.innerWidth)

  useEffect(() => {
    window.addEventListener("resize", () => {
      setWindowSize(window.innerWidth)
    })
  }, [])

  useEffect(() => {
    if (windowSize < 500) {
      setR6LogoSize('small')
      setShowSettings(false)
    } else {
      setR6LogoSize('large')
      setShowSettings(true)
    }
  }, [windowSize])

  function showAllMenu() {
    console.log("All Menu shown")
  }

  function showSettingsMenu() {
    console.log("Settings Menu shown")
  }

  return (
    <nav className="NavBar">
      <div className="AppTitle">
        <img id="r6s-logo" alt="Rainbow Six: Siege" src={`images/ui/siege-logo-${R6LogoSize}.png`}/>
        <h1>Info Suite</h1>
      </div>
      <div className="NavMenus">
        {showSettings && <NavMenuButton image="settings" navFunction={showSettingsMenu} />}
        <NavMenuButton image="menu" navFunction={showAllMenu} />
      </div>
      <ul className="NavLinks">
        <li>Operators</li>
        <li>Maps</li>
      </ul>
    </nav>
  )
}
